import React, { useState, useEffect } from 'react';

interface Cliente {
  id: number;
  nombre: string;
  apellido: string;
  email: string;
  telefono: string;
  direccion: string;
  cedula: string;
  fecha_nacimiento: string;
  tipo_cliente: 'regular' | 'vip' | 'corporativo';
  descuento_porcentaje: number;
  fecha_registro: string;
  activo: boolean;
}

interface EstadisticasCliente {
  total_compras: number;
  total_gastado: number;
  promedio_compra: number;
  ultima_compra: string;
  primera_compra: string;
}

const API_BASE = 'http://localhost:8000/api';

export const Clients: React.FC = () => {
  // Estados y funciones existentes...
  // ... (todo el c√≥digo anterior permanece igual)

  return (
    <div className="p-6">
      {/* Header y resto del c√≥digo... */}
      {/* ... (todo el c√≥digo anterior permanece igual) */}
      
      {/* Modal de detalles completo */}
      {mostrarDetalles && clienteSeleccionado && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-4xl w-full max-h-screen overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-start mb-6">
                <h2 className="text-xl font-bold text-gray-900">Detalles del Cliente</h2>
                <button onClick={cerrarDetalles} className="text-gray-400 hover:text-gray-600">
                  √ó
                </button>
              </div>
              
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                {/* Informaci√≥n personal */}
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center gap-2">
                    <Users className="h-5 w-5" /> Informaci√≥n Personal
                  </h3>
                  <div className="space-y-3">
                    <div>
                      <label className="text-sm font-medium text-gray-600">Nombre completo</label>
                      <p className="text-gray-900">{clienteSeleccionado.nombre} {clienteSeleccionado.apellido}</p>
                    </div>
                    {clienteSeleccionado.cedula && (
                      <div>
                        <label className="text-sm font-medium text-gray-600">C√©dula</label>
                        <p className="text-gray-900">{clienteSeleccionado.cedula}</p>
                      </div>
                    )}
                    {clienteSeleccionado.fecha_nacimiento && (
                      <div>
                        <label className="text-sm font-medium text-gray-600">Fecha de nacimiento</label>
                        <p className="text-gray-900 flex items-center gap-1">
                          <Calendar className="h-4 w-4" />
                          {new Date(clienteSeleccionado.fecha_nacimiento).toLocaleDateString()}
                        </p>
                      </div>
                    )}
                  </div>
                </div>

                {/* Informaci√≥n de contacto */}
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center gap-2">
                    <Phone className="h-5 w-5" /> Contacto
                  </h3>
                  <div className="space-y-3">
                    {clienteSeleccionado.telefono && (
                      <div>
                        <label className="text-sm font-medium text-gray-600">Tel√©fono</label>
                        <p className="text-gray-900 flex items-center gap-1">
                          <Phone className="h-4 w-4" />
                          {clienteSeleccionado.telefono}
                        </p>
                      </div>
                    )}
                    {clienteSeleccionado.email && (
                      <div>
                        <label className="text-sm font-medium text-gray-600">Email</label>
                        <p className="text-gray-900 flex items-center gap-1">
                          <Mail className="h-4 w-4" />
                          {clienteSeleccionado.email}
                        </p>
                      </div>
                    )}
                    {clienteSeleccionado.direccion && (
                      <div>
                        <label className="text-sm font-medium text-gray-600">Direcci√≥n</label>
                        <p className="text-gray-900 flex items-center gap-1">
                          <MapPin className="h-4 w-4" />
                          {clienteSeleccionado.direccion}
                        </p>
                      </div>
                    )}
                  </div>
                </div>

                {/* Tipo y Estado */}
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center gap-2">
                    <Award className="h-5 w-5" /> Tipo y Estado
                  </h3>
                  <div className="space-y-3">
                    <div>
                      <label className="text-sm font-medium text-gray-600">Tipo de cliente</label>
                      <p className="text-gray-900">
                        <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getTipoClienteColor(clienteSeleccionado.tipo_cliente)}`}>
                          {getTipoClienteLabel(clienteSeleccionado.tipo_cliente)}
                          {clienteSeleccionado.descuento_porcentaje > 0 && (
                            <span className="ml-1">({clienteSeleccionado.descuento_porcentaje}%)</span>
                          )}
                        </span>
                      </p>
                    </div>
                    <div>
                      <label className="text-sm font-medium text-gray-600">Estado</label>
                      <p className="text-gray-900">
                        <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${clienteSeleccionado.activo ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}>
                          {clienteSeleccionado.activo ? 'Activo' : 'Inactivo'}
                        </span>
                      </p>
                    </div>
                    <div>
                      <label className="text-sm font-medium text-gray-600">Fecha de registro</label>
                      <p className="text-gray-900 flex items-center gap-1">
                        <Calendar className="h-4 w-4" />
                        {new Date(clienteSeleccionado.fecha_registro).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                </div>

                {/* Estad√≠sticas del cliente */}
                {estadisticas && (
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center gap-2">
                      <TrendingUp className="h-5 w-5" /> Estad√≠sticas
                    </h3>
                    <div className="space-y-3">
                      <div>
                        <label className="text-sm font-medium text-gray-600">Total compras</label>
                        <p className="text-gray-900">{estadisticas.total_compras}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Total gastado</label>
                        <p className="text-gray-900">${estadisticas.total_gastado.toFixed(2)}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Promedio por compra</label>
                        <p className="text-gray-900">${estadisticas.promedio_compra.toFixed(2)}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">√öltima compra</label>
                        <p className="text-gray-900">
                          {estadisticas.ultima_compra 
                            ? new Date(estadisticas.ultima_compra).toLocaleDateString() 
                            : 'N/A'}
                        </p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Primera compra</label>
                        <p className="text-gray-900">
                          {estadisticas.primera_compra 
                            ? new Date(estadisticas.primera_compra).toLocaleDateString() 
                            : 'N/A'}
                        </p>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
